import React from 'react';
import {Grid,Typography, Avatar} from '@material-ui/core';
import Chip from '@material-ui/core/Chip';
import CodeRoundedIcon from '@material-ui/icons/CodeRounded';
import { makeStyles } from '@material-ui/core/styles';
import Education from './Education';
import photo from '../images/Resumephoto.jpg';

const useStyles = makeStyles((theme) => ({

    leftContainer:{
        margin:'1rem auto',
        height:'100%',
        direction:'column',
        textAlign:'center'
    },
    useravatar:{
        display: 'block',
        margin:'0.5rem auto',
        width: theme.spacing(30),
        height: theme.spacing(30)

      },
    rightContainer:{
        margin:'1rem auto',
        height:'100%',
        background:'black',
        color:'white'
    },
    skillHeading:{
        color:'whitesmoke',
        margin:'auto',
        opacity:0.8,
        fontSize: 48,
        fontWeight:500,
        fontFamily: "-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif",
        borderBottom:'dotted'
    },
    skills: {
        justify: 'center',
        width:'40%',
        padding:'1rem 0 1rem 4rem ',
        '& > *': {
            margin: theme.spacing(0.5),
        },
    },

}))


const skills =[{label:"ReactJS",color:"secondary"},{label:"Python",color:"primary"},
{label:"JavaScript",color:"secondary"},{label:"Html5",color:"primary"},
{label:"C#/.NET",color:"secondary"},{label:"CSS3",color:"primary"},
{label:"OpenCV",color:"secondary"},{label:"Docker",color:"primary"}]

function Resume(){

    const classes= useStyles();

    return(
        <Grid container style={{height:'100%'}}>
            <Grid container className={classes.leftContainer} justify='center'  direction='column' xs={6} sm={5} md={4}  >
                <Grid item >
                    <Avatar  className={classes.useravatar} src={photo} alt='Resume pic' />
                </Grid>

                <Grid item >
                    <Typography variant='h1' className={classes.skillHeading} >Ripudaman Kaushik</Typography>
                </Grid>

                <Grid item>
                <Typography variant='h1' className={classes.skillHeading} >Skills</Typography>
                </Grid>

                <Grid  item className={classes.skills} direction='column' >
                    {skills.map((skill) => (
                         <Chip
                         variant="default"
                         size="medium"
                         icon={< CodeRoundedIcon/>}
                         label={skill.label}
                         color={skill.color}
                         />      
                        )
                    )}
                </Grid>


            </Grid>

            <Grid container className={classes.rightContainer} justify='center' xs={6} sm={7} md={7} lg={4}>
                <Grid item>
                <Typography variant="h2" className={classes.skillHeading}>Education</Typography>
                </Grid> 


                <Education startYear="2018" endYear="2020" schoolName="Dalhousie University"
                            schoolAchievement="1. Got Academic Scholarship in 7 out of 10 subjects"
                            schoolgpa="2. 4.06 CGPA" />

                <Education startYear="2013" endYear="2017" schoolName="Punjabi University"
                            schoolAchievement="1. Got A+ in the final project presentation"
                            schoolgpa="2. 7.35 CGPA" />

            </Grid>

        </Grid>
    )
}

export default Resume
