import React,{useState} from 'react';
import { makeStyles, useTheme } from '@material-ui/core/styles';
import MobileStepper from '@material-ui/core/MobileStepper';
import Button from '@material-ui/core/Button';
import KeyboardArrowLeft from '@material-ui/icons/KeyboardArrowLeft';
import KeyboardArrowRight from '@material-ui/icons/KeyboardArrowRight';
import {useParams} from 'react-router-dom';

function importAll(r) {
    let images = {};
    r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
    return images;
  }

  const images = importAll(require.context('../images/projectimages/', false, /\.(png|jpe?g|svg)$/));


const projectStuff ={
    attendance_app:{
      pics:[images['DatabaseScreen.jpg'],images['Face404.png']],
      text:"This app was made in python and kivy"
    }, 


    watch_drone:{
        pics:[images['watchdrone1.jpg'],images['watchdrone2.jpg'],images['watchdrone3.jpg']],
        text:"This was made using React JS"
    },

} 

const useStyles = makeStyles((theme) => ({
  root: {
    maxWidth: 480,
    flexGrow: 1,
  },

  img: {
    height: 600,
    maxWidth: 480,
    overflow: 'hidden',
    display: 'block',
    width: '100%',
  },
}));

function ProjectDetails(){

  let {appName}=useParams();
  console.log(appName)

  const classes = useStyles();
  const theme = useTheme();
  const [activeStep, setActiveStep] = useState(0);
  const maxSteps = projectStuff.appName.pics.length;

  const handleNext = () => {
    setActiveStep((prevActiveStep) => prevActiveStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevActiveStep) => prevActiveStep - 1);
  };

  return (
    <div className={classes.root}>
      {projectStuff.appName.pics.map((image,index) =>(
          <img
          className={classes.img}
          src={image} />

      )      
          )}

      <MobileStepper
        steps={maxSteps}
        position="static"
        variant="text"
        activeStep={activeStep}
        nextButton={
          <Button size="small" onClick={handleNext} disabled={activeStep === maxSteps - 1}>
            Next
            {theme.direction === 'rtl' ? <KeyboardArrowLeft /> : <KeyboardArrowRight />}
          </Button>
        }
        backButton={
          <Button size="small" onClick={handleBack} disabled={activeStep === 0}>
            {theme.direction === 'rtl' ? <KeyboardArrowRight /> : <KeyboardArrowLeft />}
            Back
          </Button>
        }
      />

      <p>{projectStuff.appName.text}</p>
    </div>
  )
}




export default ProjectDetails
