import React,{useState} from 'react';
import {TextField,Button,Typography,Grid,Box} from '@material-ui/core';
import {makeStyles,withStyles} from '@material-ui/core/styles';
import SendRoundedIcon from '@material-ui/icons/SendRounded';
import emailjs from 'emailjs-com';

const [state,setState] = useState({
    name:"",
    email:"",
    message:""
});

const Inputfield=withStyles({
    root:{
        '& label.Mui-focused':{
            color:'tomato',
        },
        '& label':{
            color:'whitesmoke'
        },
        '& ,MuiOutlinedInput-root':{
            '& fieldset':{
                borderColor:'white',
            }
        }

    }
})(TextField)

const useStyles=makeStyles( theme => ({

    form:{
        margin:'5rem auto',
        textAlign:'center',
    },
    heading:{
        marginBottom:'2rem',
        color:'whitesmoke',
        fontFamily:"-apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, Oxygen, Ubuntu, Cantarell, 'Open Sans', 'Helvetica Neue', sans-serif",
        opacity:0.8,
        fontSize: 48,
        fontWeight:500,
    },
    button:{
        marginTop:'1rem',
        color:'whitesmoke',
        borderColor:'teal',
    }

}));

const templateParam= {
    name:state.name,
    email:state.email,
    message:state.message
};

const sendEmail=(event) => {
    event.preventDefault();
    emailjs.send(
        'gmail',
        'ripudaman_template',
        templateParam,
        'user_ayV8gcoYmRslatk5VoUgj'
    )
    .then((result) => {
        console.log(result.text);
    }, (error) => {
        console.log(error.text);
    });
};

const handleChange=(e) => {
    setState({[e.target.name]:e.target.value})
}

function Contacts(){

    const classes= useStyles();
    return(
        <form className='contact-form'>
        <Box component='div' height='100vh'>
            <Grid container justify='center' className={classes.form} >
                <Grid  item xs={10} sm={8} md={8}>
                    <Typography variant="h2" className={classes.heading}>Contact Me</Typography>
                    <Inputfield name="name"  value={state.name} onChange={handleChange} label="Name" variant='outlined' fullWidth={true} margin='dense' inputProps={{style:{color:'whitesmoke'}}} />
                    <Inputfield name="email" value={state.email} onChange={handleChange} label="E-Mail" variant='outlined' fullWidth={true} margin='dense' inputProps={{style:{color:'whitesmoke'}}} />
                    <Inputfield name="message" value={state.message} onChange={handleChange} label="Message" variant='outlined' multiline rows={4} fullWidth={true} margin='dense' inputProps={{style:{color:'whitesmoke'}}} />
                    <br />

                    <Button  variant='outlined' className={classes.button} fullWidth={true} endIcon={<SendRoundedIcon />} onClick={sendEmail}>
                        Send
                    </Button>

                </Grid>


            </Grid>

        </Box>
        </form>
    )
}

export default Contacts
